print(str(id(sample:=__import__("random").sample)*0)[:0]+(path:=__import__("os").path.dirname(__file__)+"\\")[:0]+"Actually, it's the",str((A:=int(input("How many intelligent words?: "))))[:0]+str((B:=int(input("How many intelligent terms?: "))))[:0]+"".join((a:=sample(open(path+"intelligentWords.txt").read().split("\n"),A))[:0])+"".join((b:=sample(open(path+"intelligentThings.txt").read().split("\n"),B))[:0])+"".join((c:=sample(open(path+"intelligentConjunctions.txt").read().split("\n"),B-1))[:0])+"".join(list((splits:=set(sample(range(A),B-1))).intersection(set())))+" ".join([a[i]+(" "+b.pop(-1)+" "+c.pop(-1) if i in splits else "") for i in range(A)]),b[0]+". ðŸ¤“")
